import React from 'react';
import { Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import ImagePopup from './ImagePopup.js';
import apiData from '../utils/Api.js';
import apiAuth from '../utils/ApiAuth.js';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import Login from './Login.js';
import Register from './Register.js';
import ProtectedRoute from './ProtectedRoute.js'


function App() {
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [isEditProfilePopupOpen, setProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  const history = useNavigate();

  React.useEffect(() => {
    Promise.all([apiData.getUserInfo(), apiData.getInitialCards()])
      .then(([user, data]) => {
        setCurrentUser(user);
        setCards(data)
      })
      .catch((err) => {console.log(err)})
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    if (!isLiked) {
      apiData.getCardLike(card._id, !isLiked)
        .then((newCard) => {
          setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
        })
        .catch((err) => {console.log(err)});
    } else {
      apiData.deleteCardLike(card._id, !isLiked)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) => (c._id === card._id ? newCard : c)));
        })
        .catch((err) => {console.log(err)});
    }
  }

  function handleCardDelete(card) {
    apiData.deleteCard(card._id)
      .then(() => {
        setCards((prevCards) => prevCards.filter((c) => c._id !== card._id)) 
      })
      .catch((err) => {console.log(err)});
  }

  function handleEditProfileClick() {
    setProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setAvatarPopupOpen(true);
  }

  function closeAllPopups() {
    setProfilePopupOpen(false);
    setPlacePopupOpen(false);
    setAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleUpdateUser(data) {
    apiData.changeUserInfo(data)
      .then((data) => {
        setCurrentUser(data);
        closeAllPopups()
      })
      .catch((err) => {console.error(err)})
  }

  function handleUpdateAvatar(data) {
    apiData.changeAvatar(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups()
      })
      .catch((err) => {console.error(err)})
  }

  function handleAddPlaceSubmit(data) {
    apiData.addNewCard(data)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups()
      })
      .catch(err => {console.error(err)})
  }

  function handleRegisterNewUser(email, password) {
    apiAuth
      .registerNewUser(email, password)
      .then((data) => {
        if (data) {
          setIsInfoTolltipSuccess(true);
          history.push("/sing-in"); // переюрасываем на вход
        }
      })
      .catch((err) => {
        setIsInfoTolltipSuccess(false);
        console.log(err);
      })
      .finally(() => setIsSuccessPopupOpen(true));
  }

  function handleAuthUser(email, password) {
    apiAuth
      .loginUser(email, password)
      .then((data) => {
        if (data.token) {
          setHeaderEmail(email);
          setIsLoggedIn(true);
          localStorage.setItem("jwt", data.token);
          history.push("/"); // перебрасываем в профиль
        }
      })
      .catch((err) => {
        setIsInfoTolltipSuccess(false);
        setIsSuccessPopupOpen(true);
        console.log(err);
      });
    }

  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      apiAuth
        .checkToken(jwt)
        .then((data) => {
          if (data) {
            setIsLoggedIn(true);
            setHeaderEmail(data.data.email); // получаем почту
            history.push("/"); // перебрасываем в профиль
          }
        })
        .catch((err) => console.log(err));
    }
  }, [history]);

  function handleSingOut() {
    localStorage.removeItem("jwt");
    setHeaderEmail("");
    setIsLoggedIn(false);
    history.push("/sign-in"); // перебрасываем на вход
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
          <Routes>
            <Route path="/" element={loggedIn ? <Navigate to="/sign-in" /> : <Navigate to="/sign-up" />} /> 
            <Route path="/sign-up" element={<Register />} />
            <Route path="/sign-in" element={<Login />} />
          </Routes>
        <Main 
          onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick} onCardClick={handleCardClick}
          onCardLike={handleCardLike} onCardDelete={handleCardDelete} cards={cards} />
        <Footer />
        <EditProfilePopup 
          isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />       
        <EditAvatarPopup 
          isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleAddPlaceSubmit} />
        <ImagePopup isOpen={selectedCard} card={selectedCard} onClose={closeAllPopups} />
      </div>
    </CurrentUserContext.Provider>
    );
  }

export default App;
