import React from "react";
import profileImage from '../images/profile_image.jpg';
import apiData from "../utils/Api.js";

function Main(props) {
  const [info, setInfo] = React.useState({
    name: "Дени Вильнев",
    about: "Режиссер, сценарист",
    avatar: false,
  });
  React.useEffect(() => {
    apiData
      .getUserInfo()
      .then((info) => {
        setInfo({
          name: info.name,
          about: info.about,
          avatar: info.avatar,
        });
      })
      .catch((err) => {
        console.log(err);
      });
    })

  const [cards, setCards] = React.useState([]);
  React.useEffect(() => {
    apiData
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className={Main}>
      <main class="content">    
        <section class="profile">
          <div class="profile__container">
            <img class="profile__image" src={info.avatar} alt="Авиатор" />
            <div class="profile__button-avatar" 
            onClick={props.onEditAvatar}></div>
              <div class="profile__info">
                <div class="profile__change">
                  <h1 class="profile__title">{info.name}</h1>
                  <button type="button" className="button profile__button-edit"
                  onClick={props.onEditProfile}></button>
                </div>
                <p class="profile__subtitle">{info.about}</p>
              </div>
            </div>
            <button type="button" className="button profile__button-add"
            onClick={props.onAddPlace}></button>
          </section>
          <section class="elements">
            <template id="elements-template">
              <div class="elements__element">
                <img class="elements__image" alt="Фото" src="#" />
                  <div class="elements__info">
                    <h2 class="elements__name"></h2>
                      <div class="elements__like-container">
                        <button type="button" class="elements__like"></button>
                        <p class="elements__likes-amount">0</p>
                      </div>
                  </div>
                  <button type="button" class="elements__delete"></button>
              </div>
            </template>             
          </section>
      </main>
    </div>
  );
}

export default Main;
