import ok from '../images/icon-ok.svg';
import notOk from '../images/icon-not-ok.svg';

function InfoTooltip(props) {
  return (
    <div className={`popup ${props.isOpen && 'popup_opened'}`}>
      {props.authStatus === 'success'
        ? (
          <div className="popup__form popup__container-login">
            <img onClick={props.onClose} alt="Закрыть" className="popup__button-close" />
            <img src={ok} alt="Все ок" className="popup__login-img" />
            <h2 className="popup__title">Вы успешно зарегистрировались!</h2>
          </div>
        ) : (
          <div className="popup__form popup__container-login">
            <img onClick={props.onClose} alt="Закрыть" className="popup__button-close" />
            <img src={notOk} alt="Все плохо" className="popup__login-img" />
            <h2 className="popup__title">Что-то пошло не так! Попробуйте еще раз.</h2>
          </div>
        )
      }
    </div>
  )
}

export default InfoTooltip;
