import ok from '../images/icon-ok.svg';
import notOk from '../images/icon-not-ok.svg';

function InfoTooltip(props) {
  return (
    <div className ={`popup popup_type_login ${props.isOpen ? "popup_opened" : "" }`}>
      <div className="popup__container popup__container_login">
        <button type="button" className="popup__button-close" onClick={props.onClose}></button>
        <div
          className={`popup__login-img ${
            props.isSuccess ? "popup__login-img_type_ok" : "popup__login-img_type_not-ok"
          }`}
        ></div>
        <h2 className="popup__title-login">
        {props.isSuccess
            ? "Вы успешно зарегистрировались!"
            : "Что-то пошло не так! Попробуйте еще раз"}</h2>
      </div>
    </div>



  )
}

export default InfoTooltip;

